tests.addTest(new Test("Dummy Precision Template Permutation",
    function() {
        var original = workerTemplateManager.originalTemplates;
        workerTemplateManager.setOriginalTemplates([dummyTemplate]);
        var inact = workerTemplateManager.getInactivesForSingleTemplateActive("-1");
        var received = [];
        for (var i = 0; i < pZs.length; i++) {
            var z = pZs[i];
            z = round(z, 5);
            pZs[i] = z;
            var data = getFakeDataScaffold();
            var temp = workerTemplateManager.getTemplate(dummyTemplate.id, z, true);
            data.lambda = temp[0];
            data.intensity = temp[1];
            data.variance = getVarianceBasedOffIntensity(data.intensity);

            data.inactiveTemplates = inact;
            var res = handleEvent(data);

            var resZ = res.results.coalesced[0].z;
            received.push(resZ);

        }
        workerTemplateManager.setOriginalTemplates(original, false);
        return received;

    }).setAbsoluteDeviationFromValue(pZs, threshold));


module.exports = tests;
